#include "libft.h"

bool	ft_is_operator(char c)
{
	return (c == '<' || c == '>' || c == '&' || c == '|' || c == ';' || c == '&'
		|| c == '@' || c == '#' || c == '%' || c == '^' || c == '*'
		|| c == '(' || c == ')' || c == '+' || c == '-' || c == '!');
}
